const {stub} = require('sinon');

exports.shell = {
    openExternal: stub().returns(true),
    openItem: stub().returns(true)
};

const browserWindow = {
    isFocused: stub().returns(true),
    show: stub(),
    focus: stub(),
};

const webContents = {
    openDevTools: stub()
};

const app = {
    getPath: stub().returns('')
}

const myGlobal = {
    config: {}
};

exports.remote = {
    getCurrentWindow: stub().returns(browserWindow),
    getCurrentWebContents: stub().returns(webContents),
    app,
    getGlobal: stub()
                .onCall('config').returns(myGlobal)
                .onCall('config_path').returns(''),
};

exports.ipcRenderer = {
    on: stub().returns(exports.ipcRenderer),
    removeListener: stub(),
};
